/**
 * This is the base class for all event types
 */
class Event {
  /**
   * required checks for the existence of the given attributes.
   * If any of the values provided are undefined, an error will be thrown
   *
   * @private
   * @param {object} attributes - key/value pair of required attributes
   */
  required(attributes) {
    for (const attribute in attributes) {
      if (!attributes[attribute] && attributes[attribute] !== 0) {
        throw new Error(`${attribute} is required`)
      }
    }
  }
}

export default Event

import Event from '../event'

/**
 * The Custom event allows you to past arbitrary events to timber.
 */
class Custom extends Event {
  /**
   * @param {String} [type] - This is the type of your event. It should be something unique       and unchanging. It will be used to identify this event.
   * @param {Array} [data] - An object containing the event data
   */
  constructor({ type, data } = {}) {
    super()

    // check for required attributes
    this.required({ type })

    // bind context attributes to the class
    this.custom = { [type]: data }
  }
}

export default Custom

import Event from '../event'

/**
 * The HTTP server request event tracks incoming HTTP requests to your HTTP server.
 */
class HTTPResponse extends Event {
  /**
   * @param {String} [body] - the body of the request
   * @param {String} [direction] - incoming or outgoing
   * @param {Array} [headers] - the headers of the request
   * @param {Object} [request] - the request object (only set if combine_http_events is true)
   * @param {String} [request_id] - the uuid of the request
   * @param {String} status - the HTTP status code
   * @param {String} time_ms - the total duration of the request in milliseconds
   */
  constructor(
    { body, direction, headers, request, request_id, status, time_ms } = {}
  ) {
    super()

    // check for required attributes
    this.required({ status, time_ms })

    // bind context attributes to the class
    this.body = body
    this.direction = direction
    this.headers = headers
    this.request = request
    this.request_id = request_id
    this.status = status
    this.time_ms = time_ms
  }

  message() {
    const parts = ['Outgoing HTTP response']

    if (this.service_name) {
      parts.push(`from ${this.service_name}`)
    }

    parts.push(`${this.status} in ${this.time_ms}ms`)

    return parts.join(' ')
  }
}

export default HTTPResponse

import attach from './utils/attach'
import errors from './data/errors'

/**
 * Installs the timber logger to route all stdout logs to the provided stream
 *
 * @param {Stream} transport - the stream that all logs will go through
 */
function install(transport) {
  if (!transport) throw Error(errors.install.noTransport)

  // attach our transport stream to stdout/stderr
  attach([transport], process.stdout)
  attach([transport], process.stderr)
}

export default install

import Augment from './utils/augment'
import config from './config'

const loggers = {
  console: {
    detect: () => config.logger.constructor.name === 'Console',
    handler: (level, message, metadata) => {
      if (metadata) {
        return config.logger[level](new Augment(message, metadata))
      }
      return config.logger[level](message)
    },
  },
  winston: {
    detect: () =>
      config.logger.Container &&
      config.logger.Logger &&
      config.logger.Transport,
    handler: (level, message, metadata = {}) =>
      config.logger.log(level, message, metadata),
  },
  bunyan: {
    detect: () => config.logger.constructor.name === 'Logger',
    handler: (level, message, metadata) => {
      config.logger[level](metadata, message)
    },
  },
}

const log = (...args) => {
  // Iterate through the loggers object to detect
  // which logger is set in the timber config.
  for (const name in loggers) {
    // If we successfully detected the logger...
    if (loggers[name].detect()) {
      // Pass the provded arguments to the logger
      return loggers[name].handler(...args)
    }
  }
}

export default log

