import bunyan from 'bunyan'
import { Writable } from 'stream'
import { Custom } from '../events'
import Augment from '../utils/augment'
import errors from '../data/errors'

/**
 * The Timber Bunyan transport allows you to seamlessly install
 * Timber in your apps that use bunyan as the logger.
 */
class BunyanTransport extends Writable {
  /**
   * @param {Object} [options] - Configuration options for the transport
   * @param {string} [options.stream] - Stream to write to
   */
  constructor({ stream, ...options } = {}) {
    if (!stream) {
      throw new Error(errors.transports.bunyan.stream)
    }

    super(options)

    this.name = 'timberBunyan'
    this.level = options.level || 'info'

    // Attach the provided stream
    this.stream = stream
  }

  /**
   * @param {buffer|string} [chunk] - The chunk to be written. Will always be a buffer unless the decodeStrings option was set to false or the stream is operating in object mode.
   * @param {string} [encoding] - If the chunk is a string, then encoding is the character encoding of that string. If chunk is a Buffer, or if the stream is operating in object mode, encoding may be ignored.
   * @param {function} [next] - Call this function (optionally with an error argument) when processing is complete for the supplied chunk.
   */
  _write(chunk, encoding, next) {
    // Parse the JSON object
    const data = JSON.parse(chunk.toString())
    const { msg, event, context, ...meta } = data
    // Convert the level integer into a string representation
    const level = bunyan.nameFromLevel[data.level]

    // Create a structured log object out of the log message
    const structuredLog = new Augment(msg, { level })

    // If custom metadata was provided with the log, append it
    if (meta && Object.keys(meta).length) {
      structuredLog.append({ meta })
    }

    // If the event key exists, append a custom event
    if (event) {
      for (const eventName in event) {
        if (!event[eventName]) continue
        structuredLog.append({
          event: new Custom({ type: eventName, data: event[eventName] }),
        })
      }
    }

    // If a context object was provided with the log, append it
    if (context) {
      structuredLog.append({
        context,
      })
    }

    // Write our structured log to the timber https stream
    this.stream.write(structuredLog.data)
    next()
  }
}

export default BunyanTransport

import https from 'https'
import { Writable } from 'stream'
import debug from '../utils/debug'
const HOSTNAME = 'logs.timber.io'
const PATH = '/frames'
const CONTENT_TYPE = 'application/json'
const USER_AGENT = `Timber Node HTTPS Stream/${require('../../package.json')
  .version}`
const PORT = 443

/**
 * A highly efficient stream for sending logs to Timber via HTTPS. It uses batches,
 * keep-alive connections (and in the future maybe msgpack) to deliver logs with high-throughput
 * and little overhead. It also implements the Stream.Writable interface so that it can be treated
 * like a stream. This is beneficial when using something like Morgan, where you can pass a custom stream.
 */
class HTTPS extends Writable {
  /**
   * @param {string} apiKey - Timber API Key
   * @param {Object} [options] - Various options to adjust the stream behavior.
   * @param {string} [options.flushInterval=1000] - How often, in milliseconds, the messages written to the stream should be delivered to Timber.
   * @param {string} [options.httpsAgent] - Your own custom https.Agent. We use agents to maintain connection pools and keep the connections alive. This avoids the initial connection overhead every time we want to communicate with Timber. See https.Agent for options.
   */
  constructor(
    apiKey,
    {
      flushInterval = 1000,
      highWaterMark = 5000,
      httpsAgent,
      httpsClient,
      hostName = HOSTNAME,
      path = PATH,
      port = PORT,
    } = {}
  ) {
    // Ensure we use object mode and set a default highWaterMark
    super({ objectMode: true, highWaterMark })
    debug('Initializing HTTPS transport stream')

    this.acceptsObject = true
    this.apiKey = apiKey
    this.hostName = hostName
    this.path = path
    this.port = port
    this.flushInterval = flushInterval
    this.httpsAgent =
      httpsAgent ||
      new https.Agent({
        maxSockets: 5,
      })
    this.httpsClient = httpsClient || https

    // Cork the stream so we can utilize the internal Buffer. We do *not* want to
    // send a request for every message. The _flusher will take care of flushing the stream
    // on an interval.
    this.cork()

    // In the event the _flusher is not fast enough, we need to monitor the buffer size.
    // If it fills before the next flush event, we should immediately flush.

    if (flushInterval !== undefined && flushInterval > 0) {
      debug('Starting stream flusher')
      this._startFlusher()
    }
  }

  /**
   * _writev is a Stream.Writeable methods that, if present, will write multiple chunks of
   * data off of the buffer. Defining it means we do not need to define _write.
   */
  _writev(chunks, next) {
    debug(`Sending ${chunks.length} log to stream`)
    const messages = chunks.map(chunk => chunk.chunk)
    const body = JSON.stringify(messages)
    const options = {
      headers: {
        'Content-Type': CONTENT_TYPE,
        'User-Agent': USER_AGENT,
      },
      agent: this.httpsAgent,
      auth: this.apiKey,
      hostname: this.hostName,
      port: this.port,
      path: this.path,
      method: 'POST',
    }

    // Add debug outputs for every possible request event
    // This should help debugging network related issues
    debug(`Instantiating req object`)
    const req = this.httpsClient.request(options, res => {
      debug(`${this.hostName} responded with ${res.statusCode}`)
      res.on('aborted', () => debug('Response event: aborted'))
      res.on('close', () => debug('Response event: close'))
    })

    req.on('abort', () => debug('Request event: abort'))
    req.on('aborted', () => debug('Request event: aborted'))
    req.on('connect', () => debug('Request event: connect'))
    req.on('continue', () => debug('Request event: continue'))
    req.on('response', () => debug('Request event: response'))
    req.on('socket', sock => {
      debug('Request event: socket')
      sock.on('close', () => debug('Socket event: close'))
      sock.on('connect', () => debug('Socket event: connect'))
      sock.on('data', () => sock.end())
      sock.on('drain', () => debug('Socket event: drain'))
      sock.on('end', () => debug('Socket event: end'))
      sock.on('error', () => debug('Socket event: error'))
      sock.on('lookup', () => debug('Socket event: lookup'))
      sock.on('drain', () => debug('Socket event: drain'))
    })
    req.on('upgrade', () => debug('Request event: upgrade'))

    req.write(body)
    req.end()
    next()
  }

  _write(chunk, encoding, next) {
    this._writev([{ chunk, encoding }], next)
  }

  /**
   * Expressive function to flush the buffer contents. uncork flushes the buffer and write
   * the contents. Cork allows us to continue buffering the messages until the next flush.
   */
  _flush() {
    // nextTick is recommended here to allow batching of write calls I think
    process.nextTick(() => {
      this.uncork()
      this.cork()
    })
  }

  /**
   * Interval to call _flush continuously. This ensures log lines get sent on this.flushInterval
   * intervals.
   */
  _startFlusher() {
    setInterval(() => this._flush(), this.flushInterval)
  }
}

export default HTTPS

import winston from 'winston'
import Augment from '../utils/augment'
import { Custom } from '../events'
import errors from '../data/errors'

/**
 * The Timber Winston transport allows you to seamlessly install
 * Timber in your apps that use winston as the logger.
 */
class WinstonTransport extends winston.Transport {
  /**
   * @param {Object} [options] - Configuration options for the transport
   * @param {string} [options.stream] - Stream to write to
   */
  constructor({ stream, ...options } = {}) {
    if (!stream) {
      throw new Error(errors.transports.winston.stream)
    }

    super(options)

    this.name = 'timberWinston'
    this.level = options.level || 'info'

    // Attach the provided stream
    this.stream = stream
  }

  /**
   * @param {string} [level] - Level of the log (info, warn, error)
   * @param {string} [msg] - The log message
   * @param {Object} [meta] - Additional metadata for the log message
   * @param {Object} [meta.event] - Event object to augment the log with
   * @param {Object} [meta.context] - Context object to augment the log with
   * @param {function} [callback] - Winston's success callback
   */
  log = (level, msg, { event, context, ...meta }, callback) => {
    // Create a structured log object out of the log message
    const structuredLog = new Augment(msg, { level })

    // If custom metadata was provided with the log, append it
    if (Object.keys(meta).length) {
      structuredLog.append({ meta })
    }

    // If the event key exists, append a custom event
    if (event) {
      for (const eventName in event) {
        if (!event[eventName]) continue
        structuredLog.append({
          event: new Custom({ type: eventName, data: event[eventName] }),
        })
      }
    }

    // If a context object was provided with the log, append it
    if (context) {
      structuredLog.append({
        context,
      })
    }

    // Write our structured log to the timber https stream
    this.stream.write(structuredLog.data)
    callback(null, true)
  }
}

export default WinstonTransport

import { Writable } from 'stream'
import Augment from '../utils/augment'
import errors from '../data/errors'
import debug from './debug'

/**
 * Attaches a transport stream to a writeable stream.
 *
 * @param {Array} transports - array of transports to attach to the stream
 * @param {Writable} toStream - the stream your transport will attach to
 * @param {Object} options - configuration options
 * @param {boolean} options.applyBackPressure
 */
const attach = (transports, toStream, { applyBackPressure = false } = {}) => {
  // Ensure all the streams are Writable
  for (let i = 0; i < transports.length; i++) {
    if (!(transports[i] instanceof Writable)) {
      throw new Error(errors.attach.notWritable)
    }
  }

  // Store refs to standard logging utilities
  const originalWrite = toStream.write

  debug(`attaching ${transports.length} transports to stream`)

  toStream.write = (message, encoding, fd) => {
    const log = message instanceof Augment ? message : new Augment(message)

    for (let i = 0; i < transports.length; i++) {
      const transport = transports[i]

      // It's possible to pass the source stream as a transport,
      // (i.e. if you want to have stdout logs output to stdout while using a transport)
      // for this reason we need to check if the transport is identical
      // to the source stream before writing to it, otherwise we'll cause a stack overflow.
      const written = transport === toStream
        ? // this condition preserves the ability to write to the original stream
          originalWrite.apply(transport, [log.data.message])
        : // and this writes to a separate transport stream
          transport.write(
            transport.acceptsObject ? log.data : log.data.message,
            encoding,
            fd
          )

      if (!written && applyBackPressure) {
        transport.once('drain', () => transport.write(...arguments))
      }
    }
  }

  return {
    detach: () => {
      toStream.write = originalWrite
    },
  }
}

export default attach

import schema from '../schema'
import config from '../config'
import errors from '../data/errors'

/**
 * Transforms a log message or object into a rich structured format
 * that timber expects, ex 'log message' @timber.io {"dt": "", "level": "info", "context": {}}
 * see https://github.com/timberio/log-event-json-schema for specs
 */
class Augment {
  /**
   * @param {String} message - the log message before transforming
   * @param {Object} [context] - context to be attached to message
   */
  constructor(message, context = {}) {
    // Throw an error if no message is provided
    if (!message) throw new Error(errors.log.noMessage)

    /**
     * Reference to original log message
     * @type {String}
     */
    this.raw = message

    /**
     * Structured log data
     * @type {Date}
     */
    this.data = {
      ...schema,
      message,
      dt: new Date(),
      ...context,
    }
  }

  /**
   * Appends data to the end of the structured log object
   *
   * @param {Object} data
   */
  append(data) {
    this.data = {
      ...this.data,
      ...data,
    }
  }

  /**
   * Convenience function for setting the log level
   *
   * @param {String} level - `info` `warn` `error` `debug`
   */
  setLevel(level) {
    this.append({ level })
  }

  /**
   * Transforms the structured log into a string
   * i.e. `Log message @metadata { ... }`
   */
  format({ withMetadata = true } = {}) {
    const { dt, ...rest } = this.data

    let message = this.raw.endsWith('\n')
      ? this.raw.substring(0, this.raw.length - 1)
      : this.raw

    if (config.timestamp_prefix) {
      message = `${dt.toISOString()} ${message}`
    }

    if (withMetadata) {
      const data = config.timestamp_prefix ? rest : { dt, ...rest }
      message += ` ${config.metadata_delimiter} ${JSON.stringify(data)}`
    }

    return `${message}\n`
  }
}

export default Augment

